/*
Get the object layer, convert the position from this layer to the screen coordinates.
Get the point on each side on the object on screen, and compare with the screen area.

This way even if the camera have a rotation or custom scale the object is always compared to the screen area.
*/


// Get the layer of the object.
const object = objects[0];
const layer = runtimeScene.getLayer(object.getLayer());

// Get the aabb of the object on his layer.
const aabb = object.getVisibilityAABB();

// Get the layer to convert the coordinates of the AABB to the screen coordinates
const topLeft = layer.convertInverseCoords(aabb.min[0], aabb.min[1]);
const topRight = layer.convertInverseCoords(aabb.max[0], aabb.min[1]);
const bottomRight = layer.convertInverseCoords(aabb.max[0], aabb.max[1]);
const bottomLeft = layer.convertInverseCoords(aabb.min[0], aabb.max[1]);

// Get the points on each side of the object on screen.
const posLeftObjectOnScreen = Math.min(topLeft[0], topRight[0], bottomLeft[0], bottomRight[0]);
const posRightObjectOnScreen = Math.max(topLeft[0], topRight[0], bottomLeft[0], bottomRight[0]);
const posUpObjectOnScreen = Math.min(topLeft[1], topRight[1], bottomLeft[1], bottomRight[1]);
const posDownObjectOnScreen = Math.max(topLeft[1], topRight[1], bottomLeft[1], bottomRight[1]);

const padding = eventsFunctionContext.getArgument("Padding");

if (
    !(posLeftObjectOnScreen - padding > runtimeScene.getGame().getGameResolutionWidth() ||
        posUpObjectOnScreen - padding > runtimeScene.getGame().getGameResolutionHeight() ||
        posRightObjectOnScreen + padding < 0 ||
        posDownObjectOnScreen + padding < 0
        )
) {
    eventsFunctionContext.returnValue = true;
}

const num = eventsFunctionContext.getArgument("num");
const arr = runtimeScene
    .getVariables()
    .get(eventsFunctionContext.getArgument("array"))
    .getAllChildrenArray();

// Technically findIndex would be more suitable but it may be a bit too recent and unsupported by some browsers
eventsFunctionContext.returnValue = arr.indexOf(arr.find(variable => variable.getAsNumber() === num));

const str = eventsFunctionContext.getArgument("str");
const arr = runtimeScene
    .getVariables()
    .get(eventsFunctionContext.getArgument("array"))
    .getAllChildrenArray();

// Technically findIndex would be more suitable but it may be a bit too recent and unsupported by some browsers
eventsFunctionContext.returnValue = arr.indexOf(arr.find(variable => variable.getAsString() === str));

const num = eventsFunctionContext.getArgument("num");
const arr = runtimeScene
    .getVariables()
    .get(eventsFunctionContext.getArgument("array"))
    .getAllChildrenArray();

// Technically findIndex would be more suitable but it may be a bit too recent and unsupported by some browsers
eventsFunctionContext.returnValue = arr.indexOf(arr.slice().reverse().find(variable => variable.getAsNumber() === num));

const str = eventsFunctionContext.getArgument("str");
const arr = runtimeScene
    .getVariables()
    .get(eventsFunctionContext.getArgument("array"))
    .getAllChildrenArray();

// Technically findIndex would be more suitable but it may be a bit too recent and unsupported by some browsers
eventsFunctionContext.returnValue = arr.indexOf(arr.slice().reverse().find(variable => variable.getAsString() === str));

const arr = runtimeScene
    .getVariables()
    .get(eventsFunctionContext.getArgument("arr"))
    .getAllChildrenArray();

eventsFunctionContext.returnValue = arr.length === 0 ? 0 :
    arr[Math.floor(Math.random() * arr.length)].getAsNumber();

const arr = runtimeScene
    .getVariables()
    .get(eventsFunctionContext.getArgument("arr"))
    .getAllChildrenArray();

eventsFunctionContext.returnValue = arr.length === 0 ? "" :
    arr[Math.floor(Math.random() * arr.length)].getAsString();

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
// Cast to array to ensure getAllChildrenArray returns the real array
arr.castTo("array");

eventsFunctionContext.returnValue = arr.getAllChildrenArray().pop().getAsNumber();

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
// Cast to array to ensure getAllChildrenArray returns the real array
arr.castTo("array");

eventsFunctionContext.returnValue = arr.getAllChildrenArray().pop().getAsString();

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
// Cast to array to ensure getAllChildrenArray returns the real array
arr.castTo("array");

eventsFunctionContext.returnValue = arr.getAllChildrenArray().shift().getAsNumber();

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
// Cast to array to ensure getAllChildrenArray returns the real array
arr.castTo("array");

eventsFunctionContext.returnValue = arr.getAllChildrenArray().shift().getAsString();

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
const target = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("target"));

// Ensure the target is a clean new array
target.castTo("array");
target.clearChildren();

// Slice the original array
const slice = arr
    .getAllChildrenArray()
    .slice(
        eventsFunctionContext.getArgument("begin"),
        // The `|| undefined` replaces 0 with undefined, as having the argument as 0 
        // would make the array empty and is therefore useless.
        // With undefined though, it doesn't stop until the end of the array,
        // which is purposeful.
        eventsFunctionContext.getArgument("end") || undefined
    );

// Copy each variable of the slice into the new array
for (const variable of slice)
    target.pushVariableCopy(variable);

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
// Cast to array to ensure getAllChildrenArray returns the real array
arr.castTo("array");
arr.getAllChildrenArray()
    .splice(
        eventsFunctionContext.getArgument("begin"),
        // Replaces 0 with the legth of the array, as having the argument as 0 
        // would not remove anything and is therefore useless.
        eventsFunctionContext.getArgument("count") || arr.getAllChildrenArray().length
    );

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
const arr2 = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr2"));
const target = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("target"));

// Ensure the target is a clean new array
target.castTo("array");
target.clearChildren();

// Copy both arrays inside the new one
for (const variable of arr.getAllChildrenArray())
    target.pushVariableCopy(variable);

for (const variable of arr2.getAllChildrenArray())
    target.pushVariableCopy(variable);

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
const target = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("target"));

// Copy both arrays inside the new one
for (const variable of arr.getAllChildrenArray())
    target.pushVariableCopy(variable);

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
// Cast to array to ensure getAllChildrenArray returns the real array
arr.castTo("array");
arr.getAllChildrenArray().reverse();

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
// Cast to array to ensure getAllChildrenArray returns the real array
arr.castTo("array");
const a = arr.getAllChildrenArray();
const end = eventsFunctionContext.getArgument("end") || a.length - 1;

for (let i = eventsFunctionContext.getArgument("begin"); i <= end; i++)
    a[i] = new gdjs.Variable({
        type: "number",
        value: eventsFunctionContext.getArgument("val"),
    });

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
// Cast to array to ensure getAllChildrenArray returns the real array
arr.castTo("array");
const a = arr.getAllChildrenArray();
a.sort(() => Math.random() - 0.5);

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
// Cast to array to ensure getAllChildrenArray returns the real array
arr.castTo("array");
const a = arr.getAllChildrenArray();
const deep = eventsFunctionContext.getArgument("deep");

for (let i = 0; i < a.length; i++)
    if (a[i].getType() === "array") {
        const b = a[i].getAllChildrenArray();
        // At the position of the array (i), remove 1 element (the array), 
        // and insert elements of b (the contents of the array) in-place of the array.
        a.splice(i, 1, ...b);
        // Skip over the elements that were just added as we are flattening only 1 level deep 
        if(!deep) i += b.length;
        // If deep, the current element has been replaced with another, we need to recheck it on next iteration.
        // If not deep, the current element is now the one after the array elements and needs to be checked too.
        // In both case, decreasing by 1 is necessary.
        i--;
    }

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
// Cast to array to ensure getAllChildrenArray returns the real array
arr.castTo("array");
const popvar = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("popvar"));

gdjs.Variable.copy(arr.getAllChildrenArray().pop(), popvar, false);

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
// Cast to array to ensure getAllChildrenArray returns the real array
arr.castTo("array");
const shiftvar = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("shiftvar"));

gdjs.Variable.copy(arr.getAllChildrenArray().shift(), shiftvar, false);

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
// Cast to array to ensure getAllChildrenArray returns the real array
arr.castTo("array");
const variable = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("variable"));

arr.getAllChildrenArray().splice(eventsFunctionContext.getArgument("index"), 0, variable.clone());

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
const str = eventsFunctionContext.getArgument("str");
const sep = eventsFunctionContext.getArgument("sep");

// Ensure the target is a clean new array
arr.castTo("array");
arr.clearChildren();

for (const string of str.split(sep)) 
    arr.pushValue(string);

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"));
const sep = eventsFunctionContext.getArgument("sep");

eventsFunctionContext.returnValue = arr
    .getAllChildrenArray()
    .reduce((accumulator, current) => accumulator + sep + current.getAsString(), "")
    // Remove first separator as it should be only between the strings
    .slice(sep.length);

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr")).getAllChildrenArray();

let min = arr[0].getAsNumber();
for (let i = 1; i < arr.length; i++) {
    const num = arr[i].getAsNumber();
    if (num < min) min = num;
}

eventsFunctionContext.returnValue = min;

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr")).getAllChildrenArray();

let max = arr[0].getAsNumber();
for (let i = 1; i < arr.length; i++) {
    const num = arr[i].getAsNumber();
    if (num > max) max = num;
}

eventsFunctionContext.returnValue = max;

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr")).getAllChildrenArray();
eventsFunctionContext.returnValue =
    arr.reduce((accumulator, current) => accumulator + current.getAsNumber(), 0) / arr.length;

const arr = runtimeScene.getVariables().get("__ArrayTools_tmp").getAllChildrenArray();
const i = arr.length / 2;
eventsFunctionContext.returnValue = 
    i % 1 == 0 
        ? (arr[i - 1].getAsNumber() + arr[i].getAsNumber()) / 2 
        : arr[Math.floor(i)].getAsNumber();

const arr = runtimeScene.getVariables().get(eventsFunctionContext.getArgument("arr"))
// Cast to array to ensure getAllChildrenArray returns the real array
arr.castTo("array");

arr.getAllChildrenArray()
    .sort((a, b) => {
        const na = a.getAsNumber();
        const nb = b.getAsNumber();
        return na < nb ? -1 : na > nb ? 1 : 0;
    });

let returnValue = true;
if (!objects.length) {
    returnValue = false;
} else {
    const objectVariableName = eventsFunctionContext.getArgument("ObjectVariableName");
    for (let i = 0; i < objects.length; i++) {
        if (!objects[i].hasVariable(objectVariableName)) {
            // If any object does NOT have the variable, return false.
            returnValue = false;
            break;
        }
    }
}

eventsFunctionContext.returnValue = returnValue;

const sourceObject = eventsFunctionContext.getObjects("SourceObject")[0];
const targetObject = eventsFunctionContext.getObjects("TargetObject")[0];
const sourceVariableName = eventsFunctionContext.getArgument("SourceVariableName");
const targetVariableName = eventsFunctionContext.getArgument("TargetVariableName");

const sourceVariables = sourceObject.getVariables();
const targetVariables = targetObject.getVariables();

if (sourceVariables.has(sourceVariableName))
{
    gdjs.Variable.copy
    (
        sourceVariables.get(sourceVariableName),
        targetVariables.get(targetVariableName)
    );
}

const sourceObject = eventsFunctionContext.getObjects("SourceObject")[0];
const targetObject = eventsFunctionContext.getObjects("TargetObject")[0];

const targetVariables = targetObject.getVariables();
const sourceVariables = sourceObject.getVariables()._variables.items;
for (const variableName in sourceVariables)
{
    if (sourceVariables.hasOwnProperty(variableName))
    {
        gdjs.Variable.copy(sourceVariables[variableName], targetVariables.get(variableName));
    }
}

const globalVariables = runtimeScene.getGame().getVariables();
const variableExist = globalVariables.has(eventsFunctionContext.getArgument("GlobalVariableName"));

if (variableExist) {
    eventsFunctionContext.returnValue = globalVariables.get(eventsFunctionContext.getArgument("GlobalVariableName")).getAsString();
}

const sceneVariables = runtimeScene.getVariables();
const variableExist = sceneVariables.has(eventsFunctionContext.getArgument("SceneVariableName"));

if (variableExist) {
    eventsFunctionContext.returnValue = sceneVariables.get(eventsFunctionContext.getArgument("SceneVariableName")).getAsString();
}

const globalVariables = runtimeScene.getGame().getVariables();
const variableExist = globalVariables.has(eventsFunctionContext.getArgument("GlobalVariableName"));

if (variableExist) {
    eventsFunctionContext.returnValue = globalVariables.get(eventsFunctionContext.getArgument("GlobalVariableName")).getAsNumber();
}

const sceneVariables = runtimeScene.getVariables();
const variableExist = sceneVariables.has(eventsFunctionContext.getArgument("SceneVariableName"));

if (variableExist) {
    eventsFunctionContext.returnValue = sceneVariables.get(eventsFunctionContext.getArgument("SceneVariableName")).getAsNumber();
}
